import React, { useState, Component } from 'react';
import { Text, View, TextInput, Button, StyleSheet, ScrollView } from 'react-native';

class CEP extends Component {
  API() {
    var URL = 'https://viacep.com.br/ws/';
    var id = '';
    var ChaveAPI = '/json/';
    const [cep, setcep] = useState(''); 
    const [logradouro, setlogradouro] = useState(''); 
    const [complemento, setcomplemento] = useState(''); 
    const [bairro, setbairro] = useState(''); 
    const [localidade, setlocalidade] = useState(''); 
    const [uf, setuf] = useState(''); 
    const [ibge, setibge] = useState(''); 
    const [gia, setgia] = useState(''); 
    const [ddd, setddd] = useState(''); 
    const [siafi, setsiafi] = useState('');   
    const [TodoJson, setTodoJson]=useState([]);
    id = textoInput;   
    fetch(URL + id + ChaveAPI)
    .then(response => response.json())
    .then(function(json){
      setcep(json.cep)
      setlogradouro(json.logradouro)
      setcomplemento(json.complemento)
      setbairro(json.bairro)
      setlocalidade(json.localidade)
      setuf(json.uf)
      setibge(json.ibge)
      setgia(json.gia)
      setddd(json.ddd)
      setsiafi(json.siafi)
      setTodoJson(JSON.stringify(json))
    })
    .catch((error) => console.error(error))
  }    
  render(){
    return (
      <ScrollView style={styles.container}>

        <View style={styles.inputGroup}>
          <TextInput 
            style={styles.Text}
            placeholder="Digite o seu CEP aqui"
          />
        </View>
        <View style={styles.containerbutton}>
          <View style={styles.button1}>
            <Button title="Pesquisar" onPress={()=>API()}></Button>
          </View>
        </View>
          <View style={styles.inputGroup}>
          <Text>{this.TodoJson}</Text>        
          <Text>Cep: {this.cep}</Text>
          <Text>Logradouro: {this.logradouro}</Text>
          <Text>Complemento: {this.complemento}</Text>
          <Text>Bairro: {this.bairro}</Text>
          <Text>Localidade: {this.localidade}</Text>
          <Text>UF: {this.uf}</Text>
          <Text>Ibge: {this.ibge}</Text>
          <Text>Gia: {this.gia}</Text>
          <Text>DDD: {this.ddd}</Text>
          <Text>Siafi: {this.siafi}</Text>
        </View>
      </ScrollView>
    );
  } 
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 5
    },
    button1:{
      paddingBottom:5,
    },
    containerbutton: {
      flex: 1,
      padding: 5,
    },
    inputGroup: {
      flex: 1,
      padding: 0,
    },
    Text:{
        height:50,
        textAlign:'center',

    }
  })
export default CEP;
