import React, { useState, Component } from 'react';
import { Text, View, TextInput, Button, StyleSheet, ScrollView } from 'react-native';
import firebase from '../database/firebaseDb';

class CEP extends Component {
  inputValueUpdate = (val, prop) => {
    const state = this.state;
    state[prop] = val;
    this.setState(state);
  }
  constructor() {
    
    super();
    this.bdREF = firebase.firestore().collection('usuarios');
    this.state = {
      cep:'',
      logradouro:'',
      complemento:'',
      bairro:'',
      localidade:'',
      uf:'',
      ibge:'',
      gia:'',
      ddd:'',
      siafi:'',
      TodoJson:'',
      isLoading: false
    };
  }  
  API() {
    var URL = 'https://viacep.com.br/ws/';
    var id = '';
    var ChaveAPI = '/json/';
    const [cep, setcep] = useState(''); 
    const [logradouro, setlogradouro] = useState(''); 
    const [complemento, setcomplemento] = useState(''); 
    const [bairro, setbairro] = useState(''); 
    const [localidade, setlocalidade] = useState(''); 
    const [uf, setuf] = useState(''); 
    const [ibge, setibge] = useState(''); 
    const [gia, setgia] = useState(''); 
    const [ddd, setddd] = useState(''); 
    const [siafi, setsiafi] = useState('');   
    const [TodoJson, setTodoJson]=useState([]);
    id = textoInput;   
    fetch(URL + id + ChaveAPI)
    .then(response => response.json())
    .then(function(json){
      setcep(json.cep)
      setlogradouro(json.logradouro)
      setcomplemento(json.complemento)
      setbairro(json.bairro)
      setlocalidade(json.localidade)
      setuf(json.uf)
      setibge(json.ibge)
      setgia(json.gia)
      setddd(json.ddd)
      setsiafi(json.siafi)
      setTodoJson(JSON.stringify(json))
    })
    .catch((error) => console.error(error))
    this.setState({isLoading: true});      
      this.bdREF.doc(this.state.Email).set({
        cep: this.state.cep,
        logradouro: this.state.logradouro,
        complemento: this.state.complemento,
        bairro: this.state.bairro,
        localidade: this.state.localidade,
        uf: this.state.uf,
        ibge: this.state.ibge,
        gia: this.state.gia,
        ddd: this.state.ddd,
        siafi: this.state.siafi,
        TodoJson: this.state.TodoJson,
      }).then((res) => {
        this.setState({
          cep:'',
          logradouro:'',
          complemento:'',
          bairro:'',
          localidade:'',
          uf:'',
          ibge:'',
          gia:'',
          ddd:'',
          siafi:'',
          TodoJson:'',
          isLoading: false,
        });
      });  
  }    
  render(){
    return (
      <ScrollView style={styles.container}>

        <View style={styles.inputGroup}>
          <TextInput 
            style={styles.Text}
            placeholder="Digite o seu CEP aqui"
          />
        </View>
        <View style={styles.containerbutton}>
          <View style={styles.button1}>
            <Button title="Pesquisar" onPress={()=>this.API()}></Button>
          </View>
        </View>
          <View style={styles.inputGroup}>
          <Text>{this.state.TodoJson}</Text>        
          <Text>Cep: {this.state.cep}</Text>
          <Text>Logradouro: {this.state.logradouro}</Text>
          <Text>Complemento: {this.state.complemento}</Text>
          <Text>Bairro: {this.state.bairro}</Text>
          <Text>Localidade: {this.state.localidade}</Text>
          <Text>UF: {this.state.uf}</Text>
          <Text>Ibge: {this.state.ibge}</Text>
          <Text>Gia: {this.state.gia}</Text>
          <Text>DDD: {this.state.ddd}</Text>
          <Text>Siafi: {this.state.siafi}</Text>
        </View>
      </ScrollView>
    );
  } 
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 5
    },
    button1:{
      paddingBottom:5,
    },
    containerbutton: {
      flex: 1,
      padding: 5,
    },
    inputGroup: {
      flex: 1,
      padding: 0,
    },
    Text:{
        height:50,
        textAlign:'center',

    }
  })
export default CEP;
