import React, { Component } from 'react';
import { Button, StyleSheet, TextInput, ScrollView, ActivityIndicator, View } from 'react-native';
import firebase from '../database/firebaseDb';

class AddUserScreen extends Component {
  constructor() {
    super();
    this.dbRef = firebase.firestore().collection('usuarios');
    this.state = {
      CPF: '',
      Nome: '',
      Email: '',
      Senha: '',
      isLoading: false
    };
  }  
  inputValueUpdate = (val, prop) => {
    const state = this.state;
    state[prop] = val;
    this.setState(state);
  }

  Cadastrar() {
    this.setState({isLoading: true});      
      this.dbRef.doc(this.state.CPF).set({
        CPF: this.state.CPF,
        Nome: this.state.Nome,
        Email: this.state.Email,
        Senha: this.state.Senha,
      }).then((res) => {
        this.setState({
          CPF: '',
          Nome: '',
          Email: '',
          Senha: '',
          isLoading: false,
        });
      });    
  }

  Atualizar() {
    this.setState({isLoading: true});      
      this.dbRef.doc(this.state.CPF).set({
        CPF: this.state.CPF,
        Nome: this.state.Nome,
        Email: this.state.Email,
        Senha: this.state.Senha,
      }).then((res) => {
        this.setState({
          CPF: '',
          Nome: '',
          Email: '',
          Senha: '',
          isLoading: false,
        });
      });
  }

  Deletar() {
    const dbRef = firebase.firestore().collection('usuarios').doc(this.state.CPF)
      dbRef.delete()
  }

  Pesquisar() {
    const doc = firebase.firestore().collection('usuarios').doc(this.state.CPF).onSnapshot(this.getCollection)
      console.log(this.getCollection);
  }

  render() {    
    if(this.state.isLoading)
    {
      return(
        <View style={styles.preloader}>
          <ActivityIndicator size="large" color="#9E9E9E"/>
        </View>
      )
    }
    return (
      <ScrollView style={styles.container}>
        <View style={styles.inputGroup}>
          <TextInput
              placeholder={'CPF'}
              value={this.state.CPF}
              onChangeText={(val) => this.inputValueUpdate(val, 'CPF')}
          />
        </View>
        <View style={styles.inputGroup}>
          <TextInput
              placeholder={'Nome'}
              value={this.state.Nome}
              onChangeText={(val) => this.inputValueUpdate(val, 'Nome')}
          />
        </View>
        <View style={styles.inputGroup}>
          <TextInput
              placeholder={'Email'}
              value={this.state.Email}
              onChangeText={(val) => this.inputValueUpdate(val, 'Email')}
          />
        </View>
        <View style={styles.inputGroup}>
          <TextInput
              placeholder={'Senha'}
              value={this.state.Senha}
              onChangeText={(val) => this.inputValueUpdate(val, 'Senha')}
          />
        </View>
        <View style={styles.containerbutton}>
            <View style={styles.button1}>
              <Button            
                title='Cadastrar'
                onPress={() => this.Cadastrar()} 
                color="#19AC52"
              />
            </View>
            <View style={styles.button1}>
              <Button
                title='Deletar'
                onPress={() => this.Deletar()} 
                color="#19AC52"
              />
            </View>            
            <View style={styles.button1}>
              <Button
                title='Atualizar'
                onPress={() => this.Atualizar()} 
                color="#19AC52"
              />
            </View>            
            <View style={styles.button1}>
              <Button
                title='Pesquisar'
                onPress={() => this.Pesquisar()} 
                color="#19AC52"
              />
            </View>   
        </View>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 35
  },
  button1:{
    paddingBottom:15
  },
  containerbutton: {
    flex: 1,
    padding: 35,
    marginBottom:15
  },
  inputGroup: {
    flex: 1,
    padding: 0,
    marginBottom: 15,
    borderBottomWidth: 1,
    borderBottomColor: '#cccccc',
  },
  preloader: {
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    position: 'absolute',
    alignItems: 'center',
    justifyContent: 'center'
  }
})

export default AddUserScreen;
