// screens/UserScreen.js

import React, { Component } from 'react';
import { StyleSheet, ScrollView, ActivityIndicator, View, Button } from 'react-native';
import { ListItem } from 'react-native-elements'
import firebase from '../database/firebaseDb';

class UserScreen extends Component {

  constructor() {
    super();
    this.firestoreRef = firebase.firestore().collection('users');
    this.state = {
      isLoading: true,
      userArr: []
    };
  }

  componentDidMount() {
    this.unsubscribe = this.firestoreRef.onSnapshot(this.getCollection);
  }

  componentWillUnmount(){
    this.unsubscribe();
  }

  getCollection = (querySnapshot) => {
    const userArr = [];
    querySnapshot.forEach((res) => {
      const { name, email, mobile } = res.data();
      userArr.push({
        key: res.id,
        res,
        name,
        email,
        mobile,
      });
    });
    this.setState({
      userArr,
      isLoading: false,
   });
  }

  render() {
    if(this.state.isLoading){
      return(
        <View style={styles.preloader}>
          <ActivityIndicator size="large" color="#9E9E9E"/>
        </View>
      )
    }    
    return (
      <ScrollView style={styles.container}>
          {
            this.state.userArr.map((item, i) => {
              return (
                <ListItem
                  key={i}
                  chevron
                  bottomDivider
                  
                  onPress={() => {
                    this.props.navigation.navigate('UserDetailScreen', {
                      userkey: item.key
                    });
                  }}><ListItem.Title>{item.name}</ListItem.Title></ListItem>
              );
            })
          }
          <View style={styles.containerbutton}>
            <View style={styles.button}>
              <Button            
                title='Cadastrar'
                onPress={() => this.props.navigation.navigate('AddUserScreen')} 
                color="#19AC52"
              />
            </View>
            <View style={styles.button}>
              <Button
                title='Deletar'
                onPress={() => this.props.navigation.navigate('AddUserScreen')} 
                color="#19AC52"
              />
            </View>            
            <View style={styles.button}>
              <Button
                title='Atualizar'
                onPress={() => this.props.navigation.navigate('AddUserScreen')} 
                color="#19AC52"
              />
            </View>            
            <View style={styles.button}>
              <Button
                title='Pesquisar'
                onPress={() => this.props.navigation.navigate('AddUserScreen')} 
                color="#19AC52"
              />
            </View>   
        </View>
      </ScrollView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
   flex: 1,
   paddingBottom: 22
  },
  button:{
    paddingBottom:15
  },
  containerbutton: {
    flex: 1,
    padding: 35,
    marginBottom:15
  },
  preloader: {
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    position: 'absolute',
    alignItems: 'center',
    justifyContent: 'center'
  }
})

export default UserScreen;
