import React, { useState } from 'react';
import { Text, View, TextInput, Button, StyleSheet, ScrollView } from 'react-native';

export default App => {

  var URL = 'https://viacep.com.br/ws/';
  var id = '';
  var ChaveAPI = '/json/';

  
  const [cep, setcep] = useState(''); 
  const [logradouro, setlogradouro] = useState(''); 
  const [complemento, setcomplemento] = useState(''); 
  const [bairro, setbairro] = useState(''); 
  const [localidade, setlocalidade] = useState(''); 
  const [uf, setuf] = useState(''); 
  const [ibge, setibge] = useState(''); 
  const [gia, setgia] = useState(''); 
  const [ddd, setddd] = useState(''); 
  const [siafi, setsiafi] = useState('');   
  const [TodoJson, setTodoJson]=useState([]);
  const [textoInput, settexto]=useState('');

function API() {
   id = textoInput;   
    fetch(URL + id + ChaveAPI)
      .then(response => response.json())
      .then(function(json){
        setcep(json.cep)
        setlogradouro(json.logradouro)
        setcomplemento(json.complemento)
        setbairro(json.bairro)
        setlocalidade(json.localidade)
        setuf(json.uf)
        setibge(json.ibge)
        setgia(json.gia)
        setddd(json.ddd)
        setsiafi(json.siafi)
        setTodoJson(JSON.stringify(json))
      })
      .catch((error) => console.error(error))
}      
  return (
    <ScrollView style={styles.container}>

    <View style={styles.inputGroup}>
        <TextInput 
            onChangeText={(text)=>settexto(text)} 
            placeholder="Digite o seu CEP aqui"
        />
    </View>
    <View style={styles.containerbutton}>
        <View style={styles.button1}>
            <Button title="Pesquisar" onPress={()=>API()}></Button>
        </View>
    </View>
    <View style={styles.inputGroup}>
        <Text>{TodoJson}</Text>        
        <Text>{cep}</Text>
        <Text>{logradouro}</Text>
        <Text>{complemento}</Text>
        <Text>{bairro}</Text>
        <Text>{localidade}</Text>
        <Text>{uf}</Text>
        <Text>{ibge}</Text>
        <Text>{gia}</Text>
        <Text>{ddd}</Text>
        <Text>{siafi}</Text>
    </View>
    </ScrollView>
  )
}
const styles = StyleSheet.create({
    container: {
      flex: 1,
      padding: 35
    },
    button1:{
      paddingBottom:15,
      height:25,
    },
    containerbutton: {
      flex: 1,
      padding: 35,
      marginBottom:15
    },
    inputGroup: {
      flex: 1,
      padding: 0,
      marginBottom: 15
    },
    preloader: {
      left: 0,
      right: 0,
      top: 0,
      bottom: 0,
      position: 'absolute',
      alignItems: 'center',
      justifyContent: 'center'
    }
  })
