import React, { useState } from 'react';
import { Text, View, TextInput, Button } from 'react-native';

export default App => {

  var URL = 'https://viacep.com.br/ws/';
  var id = '';
  var ChaveAPI = '/json/';

  
  const [cep, setcep] = useState(''); 
  const [logradouro, setlogradouro] = useState(''); 
  const [complemento, setcomplemento] = useState(''); 
  const [bairro, setbairro] = useState(''); 
  const [localidade, setlocalidade] = useState(''); 
  const [uf, setuf] = useState(''); 
  const [ibge, setibge] = useState(''); 
  const [gia, setgia] = useState(''); 
  const [ddd, setddd] = useState(''); 
  const [siafi, setsiafi] = useState('');   
  const [TodoJson, setTodoJson]=useState([]);
  const [textoInput, settexto]=useState('');

function API() {
   id = textoInput;   
    fetch(URL + id + ChaveAPI)
      .then(response => response.json())
      .then(function(json){
        setcep(json.cep)
        setlogradouro(json.logradouro)
        setcomplemento(json.complemento)
        setbairro(json.bairro)
        setlocalidade(json.localidade)
        setuf(json.uf)
        setibge(json.ibge)
        setgia(json.gia)
        setddd(json.ddd)
        setsiafi(json.siafi)
        setTodoJson(JSON.stringify(json))
      })
      .catch((error) => console.error(error))
}      
  return (

    <View style={{backgroundColor: "#BADDAD", marginTop:'15%', padding:10, borderWidth:2, margin:5}} >
        <TextInput style={{height:"20%", marginBottom:20, borderColor:"blue",borderWidth:1, textAlign:'center'}} 
        onChangeText={(text)=>settexto(text)} placeholder="Digite o seu CEP aqui"/>

        <Button title="Pesquisar" onPress={()=>API()}></Button>

        <Text>{TodoJson}</Text>        
        <Text>{cep}</Text>
        <Text>{logradouro}</Text>
        <Text>{complemento}</Text>
        <Text>{bairro}</Text>
        <Text>{localidade}</Text>
        <Text>{uf}</Text>
        <Text>{ibge}</Text>
        <Text>{gia}</Text>
        <Text>{ddd}</Text>
        <Text>{siafi}</Text>
    </View>
  )
}
